mapper.select:
SELECT R.*, O.SD_ARRAY AS SD_ARRAY, TRUNC(((R.R_SCORE_TASTE+R.R_SCORE_AMOUNT+R.R_SCORE_DELIVERY)/3),1) AS R_SCORE, (SELECT M_NICKNAME FROM MEMBER WHERE M_NO=R.M_NO) AS M_NICKNAME, (SELECT ME_NAME FROM MENU WHERE ME_NO = O.ME_NO) AS ME_NAME FROM REVIEW R JOIN ORDER_MENU O ON R.O_NO = O.O_NO WHERE R.S_NO=#{no}
AND R.R_NO IN(SELECT R_NO FROM REVIEW_IMG) AND R.R_NO = #{r_no}
ORDER BY R.R_DATE DESC
